import './CountryCard.css';
import CountryData from './CountryData'
import axios from 'axios';
import React, {useState} from 'react';

const CountryCard = () =>{
    const [data, setData] = useState([])
    const getData = () =>{
    axios.get('https://restcountries.com/v3.1/all')
        .then((response) =>{
        console.log(response.data);
        console.log(response.data[0].name.official);
        setData(response.data)
        })
        .catch((error) =>{
        // handle error
        console.log(error);
        })
        .finally( ()=> {
        // always executed
        });
    }

    return(
    <div className="card" onLoad={()=>getData()}>
        <CountryData/>
        <img alt='countryImg' id='countryImg'></img>
        <h2 id='countryName'>Country Name</h2>
        <div className='countryDescription'>
            <h3 id='population'><span>Population: </span></h3>
            <h3 id='region'><span>Region: </span></h3>
            <h3 id='capital'><span>Capital: </span></h3>
        </div>
    </div>
    )
}

export default CountryCard;



const MyComponent = ()=>{
    const [data, setData] = useState([])
    const getData = () =>{
        axios.get('https://restcountries.com/v3.1/all')
            .then((response) =>{
            console.log(response.data);
            console.log(response.data[0].name.official);
            setData(response.data)
            })
            .catch((error) =>{
            // handle error
            console.log(error);
            })
            .finally( ()=> {
            // always executed
            });
        }
    return <div>
    <button type="button" onLoad={()=>getData()}>Fetch Data</button>
    </div>
}
