import './CardDetailsPage.css'

// do i need to portal?
const CardDetailsPage = (props) => {
    let languageskeys = props.card.country.languages;
    let languages = [];

    Object.keys(languageskeys).forEach(element => {
        languages.push(languageskeys[element])
    });

    let borderkeys = "";
    let borders = [];

    props.card.country.borders ? borderkeys = props.card.country.borders : borderkeys = "";

    if (borderkeys !== "") {
        Object.keys(borderkeys).forEach(element => {
            borders.push(borderkeys[element])
        });
    }

    let currencieskeys = props.card.country.currencies;
    let currencies = [];

    Object.keys(currencieskeys).forEach(element => {
        currencies.push(currencieskeys[element])
    });

    let namekeys = props.card.country.name.nativeName;
    let names = [];

    Object.keys(namekeys).forEach(element => {
        names.push(namekeys[element])
    });

    return(
    <div className="container">
            <button>Back</button>
            <img src={props.card.country.flags.png} alt="countryImg" id='countryImg'></img>
            <h1>{props.card.country.name.official}</h1>
            <h3 className='oneline'>Native name: <span className='wide'>{names.map(name => { return <div>{name.common}</div>})}</span></h3>
            <h3>Population: <span>{props.card.country.population}</span></h3>
            <h3>Region: <span>{props.card.country.region}</span></h3>
            <h3>Sub Region: <span>{props.card.country.subregion}</span></h3>
            <h3>Capital: <span>{props.card.country.capital[0]}</span></h3>
            <br />
            <h3>Top Level Domain: <span>{props.card.country.tld[0]}</span></h3>
            <h3 className='oneline'>Currencies: <span>{currencies.map(currency => { return <div>{currency.name}</div>})}</span></h3>
            <h3 className='oneline'>Languages: <span>{languages.map(language => { return <div>{language}</div>})}</span></h3>
            <h2>Border Countries: </h2>
            <div className='borderCountries'>
                {borders.map(border => {return <div>{border}</div>})}
            </div>
    </div>
    )
}

export default CardDetailsPage;
