import "./Navbar.css";
import 'boxicons';
import { useState } from "react";

const Navbar = (props) => {
    const [darkMode, setDarkMode] = useState(false);

    let navBarClass = `NavBarContainer ${darkMode && 'dark'}`
    let moonIcon = '';

    const use = useEffect(() => {
        moonIcon = <box-icon name='moon' type={darkMode && "solid"}></box-icon>
    }, [darkMode]);


    const darkModeHandler = () => {
        if (darkMode) {
            setDarkMode(false)
            console.log("off")
        }
        else {
            setDarkMode(true);
            console.log("on")
        }

        props.onDark();
    }

    return(
        <div className={navBarClass}>
            <h1>Where in the world</h1>
            <div className="mode" onClick={darkModeHandler}>
                {moonIcon} 
                <h2 className="modetext">Dark Mode</h2>
            </div>
        </div>
    )
}

export default Navbar;
