import "./Navbar.css";
import 'boxicons';
import { useState } from "react";

const Navbar = (props) => {
    const [darkMode, setDarkMode] = useState(false);

    let navBarClass = `NavBarContainer ${darkMode && 'dark'}`

    let MoonIcon = <box-icon name='moon' {...(darkMode ? {type:"solid"}:{type:"outline"})} ></box-icon>

    const darkModeHandler = () => {
setDarkMode(prev=>!prev)


        if (darkMode) {
            setDarkMode(false);
        }
        else {
            setDarkMode(true);
        }

        props.onDark();
    }

    return(
        <div className={navBarClass}>
            <h1 className="title">Where in the world</h1>
            <div className="mode" onClick={darkModeHandler}>
                {MoonIcon}
                <h2 className="modetext">Dark Mode</h2>
            </div>
        </div>
    )
}

export default Navbar;
