import './CountryCard.css';
import CountryData from './CountryData'
import { useState } from 'react';

const CountryCard = (props) =>{

    const [countryName, setCountryName] = useState("");
    const [countryPopulation, setcountryPopulation] = useState("");
    const [countryRegion, setcountryRegion] = useState("");
    const [countryCapital, setcountryCapital] = useState("");
    const [countryPhoto, setcountryPhoto] = useState("");

    const LoadingCardsHandler = (datagot) =>{
        const countriesdata = {
            ...datagot
        }

        setCountryName(countriesdata[0].name.official);
        setcountryPopulation(countriesdata[0].population);
        setcountryRegion(countriesdata[0].continents[0]);
        setcountryCapital(countriesdata[0].capital);
        setcountryPhoto(countriesdata[0].flags.png);

        props.onLoadingPage(countriesdata);
    }

    console.log("CountryCard.js")

    return(
    <div className="card">
        <CountryData onLoadingCards={LoadingCardsHandler}/>

        <img alt='countryImg' id='countryImg' src={countryPhoto}></img>
        <h2 id='countryName'>{countryName}</h2>
        <div className='countryDescription'>
            <h3 id='population'><span>Population: </span>{countryPopulation}</h3>
            <h3 id='region'><span>Region: </span>{countryRegion}</h3>
            <h3 id='capital'><span>Capital: </span>{countryCapital}</h3>
        </div>

    </div>
    )
}


export default CountryCard;
