import './Filter.css';

const Filter = (props) => {
  let contitnentSelected = "Africa";

  let filterclassname = '';
  if (props.mode) {
    filterclassname = "dark";
  }
  else {
    filterclassname = '';
  }
  const selectHandler = (select) => {
    contitnentSelected = select.target.value;
    console.log(contitnentSelected);
    props.onFilterChange(contitnentSelected);
  }

  return(
    <div>
      <form>
        <select className={filterclassname} name="cars" id="cars" onChange={selectHandler}>
            <option value="Africa">Africa</option>
            <option value="America">America</option>
            <option value="Asia">Asia</option>
            <option value="Europe">Europe</option>
            <option value="Oceana">Oceana</option>
        </select>
      </form>
    </div>
  );
}

export default Filter;
