import "./Navbar.css";
import 'boxicons';
import { useState } from "react";

const Navbar = (props) => {
    const [darkMode, setDarkMode] = useState(false);

    let navBarClass = `NavBarContainer ${darkMode && 'dark'}`

    const darkModeHandler = () => {
        if (darkMode) {
            setDarkMode(false)
            console.log("off")
        }
        else {
            setDarkMode(true);
            console.log("on")
        }

        props.onDark();

    }

    // do on dark 

    return(
        <div className={navBarClass}>
            <h1>Where in the world</h1>
            <div className="mode" onClick={darkModeHandler}>
                <box-icon name='moon'></box-icon>
                <h2 className="modetext">Dark Mode</h2>
            </div>
        </div>
    )
}

export default Navbar;
