import "./App.css";
import Navbar from './modules/Navbar';
import SearchBar from './modules/SearchBar';
import Filter from './modules/Filter'
import CountryCard from './modules/CountryCard'
import { useState, useEffect } from "react";
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  const [darkMode, setDarkMode] = useState(false);
  let sectionclasses = `main ${darkMode && 'darkmain'}`;

  useEffect(()=>{
    axios.get('https://restcountries.com/v3.1/all')
        .then((response) =>{
        setData(response.data);
        })
        .catch((error) =>{
        })
        .finally( ()=> {
        });
  }, [])

  const dark = () => {
    if (darkMode) {
      setDarkMode(false);
    }
    else {
      setDarkMode(true);
    }
  }

  const FilterChangeHandler = (continent) => {
    console.log(continent);
    // data = data where data (all instances) .continents[0].includes(continent)
    let filterdata = data.filter(country => country.continents[0].includes(continent))
    setFilteredData(filterdata);
  }

  let Countries = () => data.map(country=>{return <CountryCard mode={darkMode} country={country} key={country.name.official}/>})

  return(
    <section className={sectionclasses}>
      <Navbar onDark={dark} />
      <SearchBar mode={darkMode} />
      <Filter mode={darkMode} onFilterChange={FilterChangeHandler} />
      {data.map(country=>{
        return <CountryCard mode={darkMode} country={country} key={country.name.official}/>
      })}
    </section>
  )
}

export default App;
