import "./App.css";
import Navbar from './modules/Navbar';
import SearchBar from './modules/SearchBar';
import Filter from './modules/Filter'
import CountryCard from './modules/CountryCard'
import { useState, useEffect } from "react";
import axios from 'axios';

function App() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [mobile, setMobile] = useState(true);

  const [darkMode, setDarkMode] = useState(false);
  let sectionclasses = `main ${darkMode && 'darkmain'}`;

  useEffect(() => {
    axios.get('https://restcountries.com/v3.1/all')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
      })
      .finally(() => {
      });
  }, [])

  const dark = () => {
    setDarkMode(prev=>!prev)
  }

  const FilterChangeHandler = (continent) => {
    setFilteredData(data.filter(country => country.continents[0].includes(continent)));
  }

  const SearchHandler = (searchInput) => {
    setFilteredData(data.filter(country => country.name.official.toLowerCase().includes(searchInput)));
  }

  let displayedCountries = filteredData.length > 0 ? filteredData : data;

  return(
    <section className={sectionclasses}>
      <Navbar onDark={dark} />
      <SearchBar mode={darkMode} onSearch={SearchHandler} />
      <Filter mode={darkMode} onFilterChange={FilterChangeHandler} />
      <div className="mobilecardContainer">
        {displayedCountries.map(country=>{
          return <CountryCard mode={darkMode} country={country} key={country.name.official}/>
        })}
      </div>
    </section>
  )
}

export default App;
