import './CardDetailsPage.css'

// do i need to portal?
const CardDetailsPage = (props) => {
    let languageskeys = props.card.country.languages;
    let languages = [];

    Object.keys(languageskeys).forEach(element => {
        languages.push(languageskeys[element])
    });

    let borderkeys = "";
    let borders = [];

    props.card.country.borders ? borderkeys = props.card.country.borders : borderkeys = "";

    if (borderkeys !== "") {
        Object.keys(borderkeys).forEach(element => {
            borders.push(borderkeys[element])
        });
    }
    if (borders.length === 0) {
        borders.push("No border countries");
    }

    let currencieskeys = props.card.country.currencies;
    let currencies = [];

    Object.keys(currencieskeys).forEach(element => {
        currencies.push(currencieskeys[element])
    });

    let namekeys = props.card.country.name.nativeName;
    let names = [];

    Object.keys(namekeys).forEach(element => {
        names.push(namekeys[element])
    });

    return(
    <div className="container">
        <div className='innercontainer'>
            <button>Back</button>
            <img src={props.card.country.flags.png} alt="countryImg" id='countryImg'></img>
            <div className='details'>
                <h1>{props.card.country.name.official}</h1>
                <p className='oneline'>Native name: <span className='wide oneline'>{names.map(name => { return <div>{name.common}</div>})}</span></p>
                <p>Population: <span>{props.card.country.population}</span></p>
                <p>Region: <span>{props.card.country.region}</span></p>
                <p>Sub Region: <span>{props.card.country.subregion}</span></p>
                <p>Capital: <span>{props.card.country.capital[0]}</span></p>
                <br />
                <p>Top Level Domain: <span>{props.card.country.tld[0]}</span></p>
                <p className='oneline'>Currencies: <span>{currencies.map(currency => { return <div>{currency.name}</div>})}</span></p>
                <p className='oneline'>Languages: <span className='oneline'>{languages.map(language => { return <div>{language}</div>})}</span></p>
                <h2>Border Countries: </h2>
                <div className='borderCountries'>
                    {borders.map(border => {return <div className='bordercountry'>{border}</div>})}
                </div>
            </div>
        </div>
    </div>
    )
}

export default CardDetailsPage;
