import './SearchBar.css';

const SearchBar = (props) => {
    let searchClass = ""
    if (props.mode) {
        searchClass = "dark";
    }
    else {
        searchClass = '';
    }


    const ChangeHandler = (input) => {
        console.log(input);
        // props.onSearch()
    }


    return(
        <div className='searchbar'>
            <input className={searchClass} type="text" placeholder='Search for a country' onChange={ChangeHandler}></input>
        </div>
    )
}

export default SearchBar;
