import "./App.css";
import Navbar from './modules/Navbar';
import SearchBar from './modules/SearchBar';
import Filter from './modules/Filter'
import CountryCard from './modules/CountryCard'
import { useState, useEffect } from "react";
import axios from 'axios';

function App() {
  const [dataLength, setDataLength] = useState([]);
  // setDataLength(1);

  useEffect(()=>{
    axios.get('https://restcountries.com/v3.1/all')
        .then((response) =>{
        // console.log('>>>>>',response.data);
        // console.log(response.data[0].name.official);
        setDataLength(response.data);
        })
        .catch((error) =>{
        // handle error
        console.log(error);
        })
        .finally( ()=> {
        // always executed
        });

        console.log("CountryCard.js");

  },[])

  const LoadingPageHandler = (datagot) =>{
      const countriesdata = {
          ...datagot
      }

      setDataLength(Object.keys(countriesdata).length);
  }

  //console.log(dataLength);
  console.log("App.js");

  return(
    <body>
      <Navbar/>
      <SearchBar/>
      <Filter/>
      {dataLength.map(country=>{

      return <CountryCard country={country} key={country.name.official} onLoadingPage={LoadingPageHandler}/>
      })}

    </body>
  )
}

export default App;
