import './CountryCard.css';
import CountryData from './CountryData'

const CountryCard = () =>{

    let countryname = "";

    const LoadingCardsHandler = (datagot) =>{
        const countriesdata = {
            ...datagot
        }
        console.log(countriesdata);
        console.log(countriesdata[0].name.official);

        countryname = countriesdata[0].name.official;
    }

    return(
    <div className="card">
        <CountryData onLoadingCards={LoadingCardsHandler}/>

        <img alt='countryImg' id='countryImg'></img>
        <h2 id='countryName'>{countryname}h</h2>
        <div className='countryDescription'>
            <h3 id='population'><span>Population: </span></h3>
            <h3 id='region'><span>Region: </span></h3>
            <h3 id='capital'><span>Capital: </span></h3>
        </div>

    </div>
    )
}


export default CountryCard;
